mapper.select:
SELECT g.id, g.`name`, g.price, g.preferential, g.grade, g.introduction, g.imagePath, gt.type_name FROM t_goods g, t_goods_type gt, t_goods_id_type git WHERE g.id = git.goods_id and gt.id = git.goods_type_id
and gt.type_name = #{goodsType}
and g.grade = #{level}

select g.id, g.`name`, g.price, g.preferential, g.introduction, img.path from t_goods g, t_goods_images img where g.id in
#{gs.id}
AND g.id = img.goods_id

select g.id, g.`name`, g.price, g.preferential, g.introduction, img.path from t_goods g, t_goods_images img where g.id in
#{userCourse.goodsId}
AND g.id = img.goods_id
